const menu=document.querySelector('.navegacionPaginas');function toggleMenu(){menu?.classList.toggle('mostrar',!menu.classList.contains('mostrar'))}
document.addEventListener("DOMContentLoaded",()=>{const titulo=document.getElementById("raqueta-titulo");const descripcion=document.getElementById("raqueta-descripcion");const carrusel=document.querySelector(".groupRaquetas-carrusel");const btnPrev=document.querySelector(".antescarrusel");const btnNext=document.querySelector(".siguientecarrusel");const indicadores=Array.from(document.querySelectorAll(".op-carrusel"));const slider=document.querySelector(".groupRaquetas-slider");const slides=Array.from(slider.children);let index=0;const raquetas=[{titulo:"Nuestras Raquetas",descripcion:"Conoce nuestras raquetas y aprende a jugar Tennis como un profesional.",fondo:"assets/img/ball-color-court-1405355.webp"},{titulo:"Raqueta Intermedia",descripcion:"Perfecta para quienes est치n mejorando su juego.",fondo:"assets/img/ball-color-court-1405355.webp"},{titulo:"Raqueta Principiante",descripcion:"Ideal para quienes est치n empezando en el tenis.",fondo:"assets/img/ball-color-court-1405355.webp"},{titulo:"Raqueta de Competici칩n",descripcion:"Dise침ada para torneos y alto rendimiento.",fondo:"assets/img/ball-color-court-1405355.webp"}];function actualizarCarrusel(){titulo.textContent=raquetas[index].titulo;descripcion.textContent=raquetas[index].descripcion;carrusel.style.backgroundImage=`url('${raquetas[index].fondo}')`;const startIndex=Math.min(index*3,slides.length-3);slides.forEach((slide,i)=>{slide.classList.toggle("visible",i>=startIndex&&i<startIndex+3);slide.classList.toggle("oculto",!(i>=startIndex&&i<startIndex+3))});indicadores.forEach((btn,i)=>{btn.classList.toggle("activo",i===index)});actualizarBotones()}
function actualizarBotones(){if(index===0){btnPrev.style.background="transparent";btnPrev.style.border="2px solid var(--white)";btnNext.style.background="var(--sickly-yellow)";btnNext.style.border="none"}else if(index===raquetas.length-1){btnNext.style.background="transparent";btnNext.style.border="2px solid var(--white)";btnPrev.style.background="var(--sickly-yellow)";btnPrev.style.border="none"}else{btnPrev.style.background="var(--sickly-yellow)";btnNext.style.background="var(--sickly-yellow)";btnPrev.style.border="none";btnNext.style.border="none"}}
btnNext.addEventListener("click",()=>{if(index===raquetas.length-1){index=0}else{index++}
actualizarCarrusel()});btnPrev.addEventListener("click",()=>{if(index===0){index=raquetas.length-1}else{index--}
actualizarCarrusel()});indicadores.forEach((btn)=>{btn.addEventListener("click",()=>{index=parseInt(btn.dataset.index,10);actualizarCarrusel()})});actualizarCarrusel()})